script:
function ButtonFunction(listview) {
    frappe.call('payment_installments.task.create_payment_installments');
    listview.refresh();
}

frappe.listview_settings['Installments'] = {
    refresh: function (listview) {
        listview.page.add_inner_button("Create Installments", function () {
            ButtonFunction(listview);
        });
    },
};
